import { decodeFunctionData, Hex } from "viem";
import { SwapRouter } from "../artifacts/SwapRouter.js";

// const USDC_ETH =
// "";
const ETH_USDC =
    "";

const result = decodeFunctionData({
    abi: SwapRouter.abi,
    data: ETH_USDC,
});

if (result.functionName === "multicall") {
    const [calls] = result.args as [Hex[]];
    calls.forEach((c) => {
        const result = decodeFunctionData({
            abi: SwapRouter.abi,
            data: c,
        });
        console.debug(result);
    });
} else {
    console.debug(result);
}
